FILE_ATTRIBUTE_HIDDEN = 0x02

def xor_encrypt_decrypt(data, key):
    extended_key = (key * (len(data) // len(key))) + key[:len(data) % len(key)]
    result = bytearray()
    for d_byte, k_byte in zip(data, extended_key.encode()):
        xor_byte = d_byte ^ k_byte
        result.append(xor_byte)
    return bytes(result)

def enc(key,file):
    try:
        with open(file, 'rb') as f:
            data = f.read()
            encrypted_data = xor_encrypt_decrypt(data, key) 
            with open(file, 'wb') as f:
                f.write(encrypted_data) 
    except:
        pass 
    
def delete():
    os.remove(filename)
    
def decrypt(key):
    for dirpath, dirnames, filenames in os.walk('.'):
        for file in filenames:
            if "fana." in file:
                continue
            full_path = os.path.join(dirpath, file)
            enc(key, full_path)
            print(f"+ {full_path} ")
        
    input("\nDecryption complete...")

def encrypt(key):
    for dirpath, dirnames, filenames in os.walk('.'):
        for file in filenames:
            if 'fana.' in file:
                continue
            full_path = os.path.join(dirpath, file)
            enc(key, full_path)
            print(f"- {full_path} ")
    with open(filename, 'wb') as f:
        f.write(xor_encrypt_decrypt(teks.encode(), key))
    try:
        ctypes.windll.kernel32.SetFileAttributesW(filename, FILE_ATTRIBUTE_HIDDEN)
    except:
        pass
    
    input("\nEncryption complete...")

def enwkey(key):
    try:
        with open(filename, 'rb') as f:
            data = f.read()
            test= xor_encrypt_decrypt(data, key).decode()
            isEncrypt = test == teks  
        if isEncrypt:
            print("> Verification successful. Running decryption...\n")
            delete()
            decrypt(key)
            return
        else:
            input("\nDecryption failed...")
            return
    except FileNotFoundError:
        print("> Encryption started...\n")
        encrypt(key)

